  `origin` varchar(128) DEFAULT NULL COMMENT '籍贯',
  `household_address` varchar(200) DEFAULT NULL COMMENT '户口详细地址',
  `emergency_contact` varchar(64) DEFAULT NULL COMMENT '紧急联系人',
  `relationship` varchar(64) DEFAULT NULL COMMENT '关系',
  `emergency_contact_phone` varchar(20) DEFAULT NULL COMMENT '紧急联系电话',
  `education` tinyint DEFAULT '0' COMMENT '学历（0-初中，1-高中，2-专科，4-本科，5-研究生，6-博士）',
  `joined_time` datetime DEFAULT NULL COMMENT '入职日期',
  `leave_time` datetime DEFAULT NULL COMMENT '离职日期',
  `create_user_id` bigint DEFAULT NULL COMMENT '创建者id',
  `create_by` varchar(64) DEFAULT NULL COMMENT '创建者',
  `update_user_id` bigint DEFAULT NULL COMMENT '修改者id',
  `update_by` varchar(64) DEFAULT NULL COMMENT '修改者',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `status` tinyint DEFAULT '0' COMMENT '状态（0-备选，10-面试，20-面试通过，30-入职申请，40-入职通过）',
  `current_address` varchar(255) DEFAULT NULL COMMENT '现住址',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  `deleted` int DEFAULT '0' COMMENT '逻辑删除字段 0:未删除 1:已删除',
  `deleteAt` datetime DEFAULT '1000-01-01 00:00:00' COMMENT '逻辑删除辅助字段',
