mapper.select:


     and ipqc_code = #{ipqcCode}
     and ipqc_name like concat('%', #{ipqcName}, '%')
     and ipqc_type = #{ipqcType}
     and template_id = #{templateId}
     and source_doc_id = #{sourceDocId}
     and source_doc_type = #{sourceDocType}
     and source_doc_code = #{sourceDocCode}
     and source_line_id = #{sourceLineId}
     and workorder_id = #{workorderId}
     and workorder_code = #{workorderCode}
     and workorder_name like concat('%', #{workorderName}, '%')
     and task_id = #{taskId}
     and task_code = #{taskCode}
     and task_name like concat('%', #{taskName}, '%')
     and workstation_id = #{workstationId}
     and workstation_code = #{workstationCode}
     and workstation_name like concat('%', #{workstationName}, '%')
     and process_id = #{processId}
     and process_code = #{processCode}
     and process_name like concat('%', #{processName}, '%')
     and item_id = #{itemId}
     and item_code = #{itemCode}
     and item_name like concat('%', #{itemName}, '%')
     and specification = #{specification}
     and unit_of_measure = #{unitOfMeasure}
     and quantity_check = #{quantityCheck}
     and quantity_unqualified = #{quantityUnqualified}
     and quantity_qualified = #{quantityQualified}
     and cr_rate = #{crRate}
     and maj_rate = #{majRate}
     and min_rate = #{minRate}
     and cr_quantity = #{crQuantity}
     and maj_quantity = #{majQuantity}
     and min_quantity = #{minQuantity}
     and check_result = #{checkResult}
     and inspect_date = #{inspectDate}
     and inspector = #{inspector}
     and status = #{status}

order by create_time desc

select pw.workorder_id, pw.workorder_code,pw.workorder_name, rp.process_id, rp.process_code,rp.process_name,mw.workstation_id, mw.workstation_code,mw.workstation_name
from pro_workorder pw
         left join pro_route_product prp
                   on pw.product_code = prp.item_code
         left join pro_route_process rp
                   on rp.route_id = prp.route_id
         left join md_workstation mw
                   on mw.process_id = rp.process_id

    and pw.workorder_id = #{workorderId}
    and pw.workorder_code = #{workorderCode}
    and mw.workstation_id = #{workstationId}
    and mw.workstation_code = #{workstationCode}

mapper.update:
update qc_ipqc
ipqc_code = #{ipqcCode}, ipqc_name = #{ipqcName}, ipqc_type = #{ipqcType}, template_id = #{templateId}, source_doc_id = #{sourceDocId}, source_doc_type = #{sourceDocType}, source_doc_code = #{sourceDocCode}, source_line_id = #{sourceLineId}, workorder_id = #{workorderId}, workorder_code = #{workorderCode}, workorder_name = #{workorderName}, task_id = #{taskId}, task_code = #{taskCode}, task_name = #{taskName}, workstation_id = #{workstationId}, workstation_code = #{workstationCode}, workstation_name = #{workstationName}, process_id = #{processId}, process_code = #{processCode}, process_name = #{processName}, item_id = #{itemId}, item_code = #{itemCode}, item_name = #{itemName}, specification = #{specification}, unit_of_measure = #{unitOfMeasure}, quantity_check = #{quantityCheck}, quantity_unqualified = #{quantityUnqualified}, quantity_qualified = #{quantityQualified}, cr_rate = #{crRate}, maj_rate = #{majRate}, min_rate = #{minRate}, cr_quantity = #{crQuantity}, maj_quantity = #{majQuantity}, min_quantity = #{minQuantity}, check_result = #{checkResult}, inspect_date = #{inspectDate}, inspector = #{inspector}, status = #{status}, remark = #{remark}, attr1 = #{attr1}, attr2 = #{attr2}, attr3 = #{attr3}, attr4 = #{attr4}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where ipqc_id = #{ipqcId}
